INSERT INTO `creeper_plants` (`id`, `name`, `description`, `price`, `type`, `sun`, `height`, `plantFolder`) VALUES
(1, 'Древогубец круглолистный<br>Celastrus orblculatus ', 'Сильно вьющееся растение с декоративными плодам. Двудомное растение женский сорт \'Диана\', мужской \'Геркулес\'. Достигает 12 м выс. (1-4 м в год). Листья закруглённые, зелёные, осенью желтеют. Плоды жёлто-красные. Нетребователен.  Рекомендуется в качестве декоративного покрытия высоких оград, не привлекательных построек и различных опор. ', '150', 'normal', 'sun', '12м', 'CelastrusOrb'),
(2, 'Кампсис укореняющийся<br>Ursynow', 'Польский сорт. Достигает 6-10 м выс. (2-4 м в год). Листья зеленые, перистосложные. Зацветает на 2-3 год. Цветки трубчатые, (3-4 см), темно-оранжевые. Карабкается при помощи цепляющихся корней и вьющихся побегов. Переносит нейтральные почвы. но нуждается в теплых защищенных местах, может подмерзать. Рекомендуется для посадки у южных стен и сильных опор.', '50-70', 'normal', 'sun', '6-10м', 'Ursynow'),
(3, 'Жимолость Брауна<br>Lonicera', 'Долгоцветущее вьющееся растение с яркими цветами. Достигает 3 м (1 м в год), цветы трубчатые, оранжево-красные. Осенью красивы оранжевые плоды. Растет на средней плодородности почвах, если они не слишком сухие или кислые. Рекомендуется высаживать у заборов и опор.', '150', 'normal', 'sun', '3м', 'Lonicera'),
(4, 'Жимолость Хекротта<br>Goldflame', 'Долго и обильноцветущая лиана. Достигает З м (1 м в год). Цветы трубчатые, розово-красные, снаружи и светло-жёлтые внутри, с нежным ароматом. Плоды багряные. Растет нейтральных почвах, если они не слишком сухие или кислые. Рекомендуется высаживать у опор. ', '100-150', 'normal', 'sun', '3м', 'LoniceraGoldflame'),
(5, 'Жимолость приморская<br>Serotina', 'Долгоцветущий сорт с душистыми цветами. Достигает 4 м (1 в год). Цветы трубчатые, фиолетово-красные снаружи, бежевые внутри. Плоды красные. Развивается на всяких почвах, если они не слишком сухие или кислые. Рекомендуется высаживать у заборов и опор. ', '100-150', 'normal', 'sun', '4м', 'LoniceraPericlymenum '),
(6, 'Жимолость Тельмана', 'Лиана, весной покрытая многочисленными жёлтыми цветками, очень интересно смотрится. Достигает 5 м (1-2 м в год). Цветки трубчатые, медно-жёлтого цвета. Плоды оранжевые. Требует свежих. плодородных почв, прикрытых мест. Рекомендуется высаживать у беседок и опор. ', '100-150', 'normal', 'sun', '5м', 'LoniceraTellmanniana'),
(7, 'Партеноциссус пятилисточковый<br>Parthenocissus quinquefolia', 'Сильная, неприхотливая лиана, цепляющаяся за стены. Достигает 20 м (1-2 м в год). Тянется вверх цепляясь усами или придерживаясь стен присосками. Листья сложные, состоят из пяти листочков, зелёные, осенью пурпурные. Нетребователен к почве. Абсолютно морозостойкое растение. Рекомендуется высаживать у заборов, стен, беседок и любых опор.', '25-100', 'normal', 'sun', '4м', 'ParthenocissusQuinquefolia'),
(8, 'Глициния обильноцветковая<br>Wisteria floribunda ', 'Цветки голубовато-фиолетовые в длинных кистевидных соцветиях. Дорастает до 10м выс. ( 1-3 м в год). Листья перистосложные, зеленые. Размноженная при помощи семян, цветет на 8-12 год, а вегетативно - на 3-4 год. Нуждается в почвах свежих, умеренноплодородных. В суровых зимах может подмерзать. Посадка в защищенных местах, теплых, около сильных опор. ', '80', 'normal', 'sun', '10м', 'WisteriaFloribunda ');

