INSERT INTO `d_shrubs` (`id`, `name`, `description`, `price`, `type`, `sun`, `height`, `plantFolder`) VALUES
(1, 'Барбарис Тунберга<br>Atropurpurea Nana', 'Карликовый, колючий кустарник с компактной плоскоокруглой кроной. высотой до 0,6 м и шир. 1 м. Листья темно-пурпурные. Осенью окрашивается в красный цвет. Цветки жёлтые. Плоды ярко-красные, блестящие. Хорошо растет на любых почвах: от слабокислых до умеренно щелочных. Зимостоек. Хорошо переносит стрижку. Подходит для рабаток, миксбордеров и каменистых садов.', '80-250', 'normal', 'sun', '0.6м высота, 1м ширина', 'atroNana'),
(2, 'Барбарис<br>Юлиана', 'Вечнозелёный кустарник выпрямленной формы. Вырастает до 2-3 метров высотой. Побеги покрыты острыми шипами. Листья кожистые, тёмно-зелёные. Растёт на почве от умеренно сухой до влажной, от кислой до щелочной. Рекомендуется для садов, парков и городских насаждений. Высаживают в местах, защищенных от ветра. В суровые зимы может подмерзать.', '80-250', 'normal', 'sun', '2-3м', 'julian'),
(3, 'Барбарис Тунберга<br>Helmond Pillar', 'Кустарник узкой колоновидной формы. Достиrает высотой. 1,5 м и шир. 0,4-0,6 м. Листья опадающие, пурпурно-красные, мелкие. Цветки желтые, меленькие, ветки колючие. Морозостоек и нетребователен к почвам и условиям выращивания. Прекрасная форма для узких шпалер или как солитерный акцент в композиции.', '80-250', 'normal', 'sun', '0.6м ширина, 1.5м высота', 'helmondPillar'),
(4, 'Барбарис Тунберга<br>Red Chief', 'Быстрорастущий колючий кустарник с развесистой кроной и поникшими ветвями, высотой и диаметром кроны до 2 м. Листья от ланцетных до обратнояйцевидных, пурпурно-коричневыстые, осенью багряные. Плоды розовые, сохраняются всю зиму. К почве нетребователен. Подходит рля живых изгородей, шпалер, цветовых композиций.', '80-250', 'normal', 'sun', '2м', 'redChief'),
(5, 'Дёрен белый<br>Elegantissima', 'Крупный, раскидистый кустарник с декоративными листьями и побегами, достигает 3 м выс. и подобной шир. Когда двухцветные, бело-зелёные листья опадают на зиму, то эффектно смотряться красные побеги. Хорошо переносит щелочную почву и временное подтопление. Даёт мощный цветовой акцент, рекомендуется для любого сада и парка.', '50-250', 'normal', 'sun', '3м', 'Elegantissima'),
(6, 'Хеномелес японский', 'Низкий раскидистый колючий кустарник до 1 м высотой. Цветки крупные, кирпично-красные в щитках по 2-4, распускаются до появления листьев. Плоды жёлтые, шаровидные, приплюснутые, с приятным ароматом зелёных яблок, очень декоративные, съедобные. Нетребователен к почве, но лучше растет на влажной и плодородной. Идеален для одиночных, групповых и бордюрных посадок.', '50-150', 'normal', 'sun', '1м', 'henomeles'),
(7, 'Красивоплодник Бодиньера<br>Profusion', 'Листопадный кустарник с декоративными плодами до 2-3 м высотой. Листья матовые, зелёные, осенью жёлтые. Цветки невзрачные, многочисленные, даже на молодых растениях. Ягоды блестящие от пурпурных до фиолетовых, собранные в шарообразные гроздья. Требует тёплого и защищённого места. Неморозостоек, но хорошо восстанавливается после обрезки. Высаживают большими группами в связи с перекрестным опылением.', '150', 'not_exist', 'sun', '2-3м', 'profusion'),
(8, 'Буддлея Давида<br>White Profucion', 'Буйный кустарник достигающий 3 м высотой. Белые цветы собраны на концах побегов в султаны, длиной 30-50 см, привлекающие своим запахом бабочек. Хорошо растет на возделываемых, плодородных садовых почвах. Цветение на годовалых побегах. Для обильного цветения желательна ежегодная сильная обрезка.', '80-150', 'normal', 'sun', '3м', 'whiteProfusion'),
(9, 'Кизильник горизонтальный', 'Низкий раскидистый кустарник, с почти горизонталь-ными побегами и характерным, похожим на рыбий хребет, ветвлением. Достигает 1 м высотой. и двукратную ширину. Листья блестящие, тёмно-зелёные, осенью окрашиваются в оранжевый цвет. Цветки бело-розовысотой. Красные ягоды, долго остаются на кусте. Растёт на всех видах садовой почвы. Рекомендуется для рокариев, на подпорные стенки и откосы.', '80-250', 'normal', 'sun', '1м', 'cotoneaster'),
(10, 'барбарис Тунберга<br>Maria', 'Густой колючий куст с прямыми побегами, достигает 1,2-1,5 м высотой. Листья крупные, золотисто-желтые, карминовым окаимлением, осенью окрашиваются в оранжево-красный. Крупные, красные, декоративные плоды. Для полной окраски нуждается в солнечном месте, предпочитает плодородную почву. Рекомендуется одиночно, в группах, для изгородей и контейнеров. Хорошо переносит стрижку.', '80-250', 'normal', 'sun', '1.2-1.5м', 'maria'),
(11, 'Дейция шершава<br>Candidissima', 'Кустарник с поднятыми вверх твердыми ветвями. высотой. 2-3 м. Темно-зеленые, узко-яйцевидные листья с обеих сторон жесткоопушенные. Цветки махровысотые, снежно-белые, бутоны светло-розовысотые, собраны в стройные соцветия. Почва средняя, но постоянно увлажненная. Прекрасно подходит для садов и парков одиночно или в группах. В суровысотой зимы может подмерзать до уровня снега.', '80-150', 'normal', 'sun', '2-3м', 'candidissima'),
(12, 'Бересклет европейский<br>Red Cascade', 'Кустарник широкой конусовидной формы высотой. 2-3 м. Побеги зеленые или буро-зеленые, ребристые, вдоль которых идут 4 узких пробковых нароста. Листья эллиптические, осенью пурпурные. Цветки светлозеленые, мелкие, невзрачные. Плоды с оранжевым околоплодником, ядовиты. Семена покрыты оранжевой оболочкой. Прекрасно подходит для образования естественных пейзажных групп и изгородей.', '50-200', 'not_exist', 'sun', '2-3м', 'redCascade'),
(13, 'Гортензия древовидная<br>AnaЬelle', 'Декоративный цветущий кустарник с разбросанными ветвями, достигает 1-1,5 м высотой. Листья светло-зелёные. Цветки кремово-белые, собраны в большие щитковидные соцветия. Растёт в защищённых, солнечных и затенённых местах. Молодые, быстрорастущие растения, часто требуют подвязки для поддержания соцветий. Рекомендуется для приусадебных участков.', '50-200', 'normal', 'sun', '1-1.5м', 'annabelle'),
(14, 'Гибискус сирийский', 'Прямой, медленнорастущий кустарник, достигающий 1,5-2 м высотой. Листья опадающие, весной появляются поздно, осенью жёлтые. Цветы декоративные до 10 см диам., одиночные, похожие на цветки мальвы. Сорта различаются между собой по окраске цветов, например: \'Ardens\' пурпурный, \'Hamabo\' розовый, \'Тotus Albus\' белый. Требует перегнойной, проницаемой почвы. Может полностью вымерзнуть в суровую зиму, поэтому нуждается в защищённом месте с дополнительным укрытием на зиму.', '50-150', 'normal', 'sun', '1.5-2м', 'gibiskus'),
(15, 'Бересклет Форчуна<br>Emerald\'n Gold', 'Вечнозелёный низкий кустарник со стелющимися побегами. Может достигнуть 0,4 м высотой. и 0,8 шир. Листы тёмно-зелёные с доминирующей жёлтой каймой. Произрастает на всех видах возделываемой почвы, под кронами деревьев на высоте. до З м. Рекомендуется для контейнеров.', '50-120', 'normal', 'sun', '0.4м', 'emeraldGold'),
(16, 'Бересклет Форчуна<br>Emerald Gaiety', 'Вечнозелёный ползучий кустарник со стелющимися побегами. На открытых пространствах достигает 0,25 м вые. Листья зелёные с белой каймой. Растет на всех видах перегнойных садовых почв, под кронами больших деревьев. Образует густые ковры. В защищённых местах может карабкаться на деревья, стены, камни на вые. до 2 м.', '50-120', 'normal', 'sun', '0.25м', 'emeraldgaiety'),
(17, 'Hydrangea panicula', 'Кустарник с прямымм мощными ветвями. Дopаcтает до высоты 3 м. Листья зеленые. Цветки собраны в крупные вершинные соцвет-метелки, изначально зеленовато-жетые, позднее кремово-белые. Почвы богатые, гумусовысотой, умеренновлажные. Сажать одиночно или в групах в парках и садах в местах. защищенных от ветра. Морозостойка.', '50-200', 'normal', 'sun', '3м', 'hydrangeapaniculata'),
(18, 'Гортензия дуболистная', 'Широкий кустарник высотой. до 1,5 м, разрастается в шир. благодаря отпрысковым побегам. Листья крупные, дубовидные, снизу опушенные, осенью красно-фиолетовысотой, остаются на ветвях до поздней осени. Цветки белые, в длинных, пышных соцветиях. Любит плодородные, умеренновлажные и слегка кислые почвы. Неморозостоек, нуждается в укрытии на зиму и тихом месте.', '100-300', 'normal', 'sun', '1.5м', 'gortenziya'),
(19, 'Керрия японская', 'Пряморастущий кустарник с множеством по-бегов, разрастающийся корневыми отпрысками. Достигает 1,5-2 м высотой. и шир. Побеги зелёные, гладкие, декоративные зимой. Листья зелёные рано распускаются. Цветки жёлто-золотые. Растёт на солнечных, и затенённых местах, а также на всех видах дренированной, рыхлой и не очень сухой почвы.', '80-150', 'normal', 'sun', '1.5-2м', 'kerria'),
(20, 'Керрия японская<br>Picta', 'Небольшой листопадный кустарник до 1м в высоту с прямыми, зелёными, гладкими побегами, декоративными зимой. Листья серо-зелёные с белой каймой. Цветки золотисто-желтые. Хорошо растёт на любой хорошо дренированной почве. Морозоустойчив, эффектен весь год. Рекомендуется для малых садов, составления цветовых композиций.', '80-150', 'normal', 'sun', '1м', 'picta'),
(21, 'Вейгела<br>Red Prince', 'Компактный кустарник высотой. до 1,5 м. Листья зеленые. Цветки колокольчиковидные, ярко-красные. Предпочитает почвы плодородные, умеренновлажные, пропускаемые. Эффектный кустарник для посадки в группах и одиночно, на живысотой цветущие изгороди, которые можно формировать после окончания цветения.', '80-250', 'normal', 'sun', '1.5м', 'redPrince'),
(22, 'Вейгела цветущая<br>Variegata', 'Широкораскидистый кустарник до 2,5 м высотой. и значительно большей шир. Листья серо-зелёные с узкой жёлто-белой каймой. Цветки колокольчатые белорозовысотой. Предпочитает влажные, богатые, проницаемые почвы. Для посадки в группах, одиночно, контрастных и цветовых композиций.', '80-250', 'normal', 'sun', '2.5м', 'variegataVeigela'),
(23, 'Спирея японская<br>Goldflame', 'Небольшой густой полукруглый кустарник до 0,8 высотой. Молодые листья медно-оранжевые, поздних: зелёно-жёлтые.  Неприхотливый для всех видов почв.', '80-250', 'normal', 'sun', '0,8м', 'goldflame'),
(24, 'Бузина чёрная<br>Eva', 'Большой пряморастущий кустарник, иногда небольшое дерево с куполовидной кроной. Достигает 3 м высотой. Листья разрезаны, как у Laciniata, но темно-пурпурного цвета.', '80-250', 'normal', 'sun', '3м', 'eva'),
(25, 'Бузина чёрная<br>Aurea', 'Крупный прямой кустарник, иногда небольшое дерево с куполообразной кроной. Достигает 3 м высотой. Мола-.. дые листья насыщенного желтого цвета, старшие жёлто-эелёного, рано распускаются весной. Цветки кремово-белые, в плоских соцветиях. Плоды чёрные. Неприхотливый кустарник с интересной цветовой гаммой, для посадки в парках.', '80-250', 'normal', 'sun', '3м', 'aurea'),
(26, 'Чубушник венечный', 'Упругий, прямой кустарник с декоративными цветами. Вые. 2-3 м. Листья матово-зелёные, немного шершавые. Цветки белые, в кистях по 5-9 штук или одиночные, с сильным запахом, V-VI. Растёт на солнце и в лёгкой тени. Морозоустойчив, неприхотлив, хорошо растёт на любых садовых почвах. Для посадки ~оодному или в группах, создания живых изгородеи и шпалер.', '50-100', 'normal', 'sun', '2-3м', 'chubushnik'),
(27, 'Слива цистена', '-', '-', 'not_exist', 'sun', '-', 'cistena');

