INSERT INTO `flower_plants` (`id`, `name`, `description`, `price`, `type`, `sun`, `height`, `plantFolder`) VALUES
(1, 'Тысячелистник аrератолистный<br>Achillea ageratifolia ', 'Низкое растение с узколанцетными листьями, покрытых белым войлочным опушением Образует очень эффектные подушки. Высота во время цветения 10-20 см. Нуждается в солнечных местах и пропускаемой почве, содержащей известь. Соцветия корзинковидные, белые, диам. 2,5 см. Используется на альпийских горках.', '50', 'normal', 'sun', '10-20см', 'ageratifolia'),
(2, 'Тысячелистник таволговый<br>Achillea filipendulina ', 'Высокий кустовидный многолетник с прямыми жёсткими побегами. выс. до 1,2 м. Цветки собраны в ярко-жёлтые плоские цветочные корзинки. Нуждается в плодородной проницаемой почве. Используется на больших цветниках вместе с другими многолетниками. Свежие и сухие цветы используют букетах. ', '35-50', 'normal', 'sun', '1.2м', 'filipendulina'),
(3, 'Тысячелистник обыкновенный<br>Cerise Queen', 'Разновидность повсеместно встречающегося тысячелистника с красивыми глубокорассеченными тёмно-зелеными листьями, твердыми пряморастущими по-бегами. Соцветия в виде плоских тарелочек бывают от ярко-розовой до тёмно-розовой окраски, выс. до 80 см. Необходимо своевременно удалять отцветающие соцветия, чтобы продлить период цветения и не допустить самосева. Подходит для рабаток и срезки. ', '35-50', 'normal', 'sun', '80см', 'CeriseQueen'),
(4, 'Армерия приморская<br>Alba', 'Сорт с белыми цветками. В отличие от других разновидностей более устойчив к неблагоприятным условиям, особенно зимой, поэтому сохраняет красивую свежую темно-зеленую листву до весны. Хорошо подходит для покрытия небольших поверхностей. ', '35-50', 'normal', 'sun', '15см', 'Alba'),
(5, 'Райграс луковичный<br>Variegatum', 'Декоративный злак выс. до 40 см, в благоприятных условиях может быть выше. Имеет подземные побеги с клубеньковыми образованиями. Рекомендуется для вересковых садиков и натуральных посадок. В условиях недостаточной влажности почвы над· земная часть замирает раньше. Листья с белыми полосками. Не разрастается так агрессивно, как другие пестролистные злаки, поэтому можно использовать в качестве бордюров или на альпийских горках. ', '35-50', 'normal', 'sun', '40см', 'Variegatum'),
(6, 'Полынь<br>Artemisia ludoviciana', 'Растение с негнущимися, раэветвлёнными в верхнеи части побегами, выс. 60-90 см. Листья сильноразрезанные, все растение покрыто серо-белым опушением. Корзинки цветов мелкие, коричневатые, после отцветания долго смотрятся эффектно, благодаря привлекательной форме и бело-серой окраске лепестков. Разрастается при помощи подземных побегов, образовывая густые нивы. В случае интенсивного разрастания растение необходимо ограничивать.', '35-50', 'normal', 'sun', '60-90см', 'ludoviciana'),
(7, 'Бадан сердцелистный<br>Bergenia cordifolia ', 'Многолетник с большими, округлыми, кожистыми листьями, осенью краснеющими. Полувечнозелёный. Появляются крупные красные побеги, выс. до 40 см, с густыми соцветиями, сначала шаровидной, а потом зонтичной формы, цветки лилово-розовыс. К почвам не требователен. Лучше растёт в полутени, на влажной почве. Подходит для посадки в рокариях, рядом с водоемами и для задернения. ', '100-250', 'normal', 'sun', '40см', 'cordifolia'),
(8, 'Колокольчик ложечницелистный<br>Campanula cochleariifolia ', 'Низкий изящный горный многолетник выс. 10 см. Цветки мелкие колокольчатые, поникшие, голубого цвета. Удаление отцветших цветков стимулирует повторное цветение. Лучше всего растёт на проницаемой, но влажной почве в альпинариях. ', '35-50', 'normal', 'sun', '10см', 'cochleariifolia'),
(9, 'Колокольчик Портеншлага<br>Campanula portenschlagiana ', 'Один из лучших колокольчиков для подпорной стенки. Образует подушки выс. 10 см. Цветёт очень обильно и долго. Образует подземные по беги, не агрессивен по отношению к соседям. Цветки колокольчатые, голубовато-фиолетовые, повёрнутые вверх. Нетребователен, переносит небольшую засуху и затенение. ', '35-50', 'normal', 'sun', '10см', 'portenschlagiana'),
(10, 'Ясколка войлочная<br>Cerastium tomentosum', 'Сильноразрастающееся стелющееся растение. Побеги покрыты почти белыми, войлочными листиками. Цветки белые, по нескольку на верхушках цветоносов выс. до 15 см. Привлекательно благодаря окраске листьев. Растет даже на бедных почвах. После отцветания рекомендуется соцветия обрезать. Из-за интенсивного разрастания рекомендуется только для больших альпийских горок и садов. ', '35-50', 'normal', 'sun', '15см', 'tomentosum'),
(11, 'Гвоздика серовато-голубая<br>Dianthus gratianopolitanus ', 'Образует густые подушки голубовато-зелёных листьев, выс. 15 см. Цветки, чаще всего розовые, с растрёпанными на концах лепестками, ароматные, на стройных побегах, выс. до 30 см. Требует очень рыхлой почвы, хорошо переносит засуху. Рекомендуется на передний план рабатки, для альпинариев. ', '35-50', 'normal', 'sun', '30см', 'gratianopolitanus'),
(12, 'Дицентра великолепная<br>Dicentra spectabilis ', 'Ранней весной появляются сильные красноватые дугообразные побеги с красивыми перистыми листьями, с которых свисают необычной формы сердцевидные бело-розовые цветки. В середине лета надземная часть отмирает. необходимо тщательно подобрать соседей. Очень долговечна. ', '100-150', 'normal', 'sun_shadow', '70см', 'spectabilis'),
(13, 'Эриrерон<br>Dunkelste Aller', 'Слабоветвящееся растение, выс. до 50 см, с несколькими цветковыми корзиночками на верхушке. Цветки в корзиночках язычковые, очень узкие, тёмно-фиолетовые, после обрезки цветет второй раз. Нуждается в плодородной легкой почве, не переносит засухи. Подходит для рабаток и для срезки. ', '50', 'normal', 'sun', '50см', 'DunkelsteAller'),
(14, 'Молочай многоцветн<br>Euphoria polychroma ', 'Образует правильные полушаровидные кустики выс. до 40 см,покрывается соцветиями с пышными, жёлто-золотыми околоцветниками. Нетребователен к почве. После цветения рекомендуется срезать надземную часть. Используется на рабатках и альпинариях.  ', '50', 'normal', 'sun', '40см', 'polychroma'),
(15, 'Овсяница голубая<br>Festuca glauca ', 'Вечнозелёная трава образующая густые голубоватые кочки. Может размножаться самосевом, если не удалять соцветия. Используется для альпинариев, контейнерного озеленения и естественных композиций. ', '50', 'normal', 'sun', '20-25см', 'Festucaglauca'),
(16, 'Хоста<br>Stiletto', 'Невысокий сорт с очень узкими зелёными листьями, широким, почти белым окаймлением, которое заметно во время всего вегетативного периода. Цветки небольшие голубовато-фиолетовые, появляются. Идеальное растение для розария, не переносит прямых солнечных лучей.', '50', 'normal', 'shadow', '15см', 'Stiletto'),
(17, 'Хоста<br>Sum and Substance', 'Роскошная разновидность, выс. до 75 см, с огромными светло-жёлтыми (в затенённых местах зеленовато-жёлтыми) листьями. Довольно стойкая на солнце, но лучше высаживать в места без прямого солнечного света, но хорошо освещённых. Нуждается в плодородной почве и дополнительных удобрений. Хорошо смотрится в одиночной посадке или небольшими группами.', '80', 'normal', 'shadow', '75см', 'Substance'),
(18, 'Иберис вечнозелёный<br> lberis sempervirens ', 'Вечнозеленый кустарничек, выс. до 30 см, покрывающийся многочисленными белыми цветками. После цветения соцветия срезаются, одновременно можно поправить силуэт растения. Требует проницаемой, плодородной, не очень влажной почвы. Подходит для альпинариев и на цветочные бордюры. В бесснежные зимы может подмерзать.', '75', 'normal', 'sun', '30см', 'sempervirens'),
(19, 'Ирис сибирский<br>Iris sibirica', 'Вырастает до выс. 100 см, листья узкие, зелёные, поникающие. Цветки голубые, белые или фиолетовые, в зависимости от сорта, на стройных побегах возвышающихся над листьями. Используют на цветочных рабатках, у водоемов.', '50', 'normal', 'sun', '100см', 'Irissibirica'),
(20, 'Лиатрис колосковый<br>Liatris spicata ', 'Вырастает до выс. 100 см, листья узкие, зелёные, поникающие. Цветки голубые, белые или фиолетовые, в зависимости от сорта, на стройных побегах возвышающихся над листьями. Используют на цветочных рабатках, у водоемов.', '25', 'normal', 'sun', '70см', 'Liatrisspicata'),
(21, 'Вербейник монетчатый<br>Lysimachia nummularia ', 'Стелющийся по земле многолетник с побегами  до 60 см, имеет круглые, светло-зелёные листья и звездообразные, желтые цветки. Растет на влажном месте. Чем больше влаги в почве, тем лучше переносит солнечные лучи. Высаживается У воды, подходит для эадернения тенистых мест.', '25-35', 'normal', 'sun', '60см', 'nummularia'),
(22, 'Энотера миссурийская<br>Oenothera macrocarpa ', 'Имеет прилегающие к земле побеги, выс. растения не превышает 30 см. Появляются очень крупные жёлтые цветки диам. до 12 см, которые раскрываются вечером. Плоды декоративные, используются дnя сухих букетов. Нуждается в очень рыхлой почве. Под-ходит для каменистых садов.', '75', 'normal', 'sun', '30см', 'macrocarpa');

