INSERT INTO `needle_plants` (`id`, `name`, `description`, `price`, `type`, `sun`, `height`, `plantFolder`) VALUES
(1, 'Можжевельник китайский<br>Obelisk	', 'Форма кроны неправильная, сначала узкоконическая, позднее широковатая. В 10 лет достигает 3 метра в высоту. Хвоя шиловидная, жёсткая, покрыта синим налётом. Побеги прямостоячие, верхушки слегка восходящие. Требования к почве и влаге невысокие. Рекомендуется высаживать в шпалеры, на кладбища и в садах в составе разных композиций.', '100-1000', 'normal', 'sun', '3м', 'obelisk'),
(2, 'Можжевельник китайский<br>Stricta ', 'Густой конический, медленнорастущий сорт можжевельника, достигающий в возрасте 10 лет 2 метров роста. Побеги растут вертикально. Хвоя зелёно-голубого цвета, колючая. Требования к почве и влаге невысокие. Рекомендуется для розариев, вересковых садов, контейнерного озеленения.', '100-1000', 'normal', 'sun', '2м', 'strikta'),
(3, 'Можжевельник обыкновенный<br>Arnold', 'Узкоколоновидная, компактная,  медленнорастущая форма, в 10 лет достигает не более 1,5 метров в высоту. Побеги вертикальные, хвоя голубовато-зелёная, колючая. Небольшие требования к почве и влаге. Отличный сорт для альпинариев и вересковых садов, озеленения кладбищ.', '100-1000', 'normal', 'sun', '1.5м', 'arnold'),
(4, 'Можжевельник обыкновенный<br>Green Carpet', 'Карликовый, свободнорастущий, стелящийся кустарник, достигающий в возрасте 10 лет 10 см в высоту при диаметре 1,5 метра. Хвоя светло-зелёного цвета, мягкая, неколючая. Не требователен. Рекомендуется для каменистых садов а также как почвопокровное растение. Плотность посадки 3 шт/м2 .', '100-1000', 'normal', 'sun', '10см высота, диаметр 1.5м', 'greenCarpet'),
(5, 'Можжевельник обыкновенный<br>Hibernica', 'Колоновидная, очень распространенная форма можжевельника обыкновенного. Сильнорослый, в возрасте 10 лет достигает 2 метра в высоту. Хвоя короткая, голубовато-синего цвета. Требования к влаге и почве небольшие. На зиму растения необходимо связать, чтобы предотвращать поломку ветвей от тяжести снега. Рекомендуется для небольших приусадебных и вересковых систем, кладбищ.', '100-1000', 'normal', 'sun', '2м', 'hibernica'),
(6, 'Можжевельник обыкновенный<br>Horstmann', 'Оригинальная, раскидистая и живописная форма с распростертыми побегами, плотно прижатыми к земле. В возрасте 10 лет достигает 1,5 метра в высоту и столько же в диаметре. Горизонтальные, поникающие на концах побеги, придают более старым растениям прекрасный плакучий вид. Требования к почве и влаге небольшое, рекомендуется высаживать одиночно на газонах.', '100-1000', 'normal', 'sun', '1.5м', 'horstmann'),
(7, 'Можжевельник прибрежный<br>Blue pacific ', 'Раскидистая форма, медленнорастущая, достигает в возрасте 10 лет около 0,2 метра в высоту при диаметре 1 метр. Иглы длинные, голубые, колючие. Не требовательная. Используется как почвопокровник, на альпийских горках и альпинариях.', '100-1000', 'normal', 'sun', '0.2м высота, диаметр 1.5м', 'bluePacific'),
(8, 'Можжевельник горизонтальный<br>Blue Chip', 'Невысокий, стелящийся кустарник, характеризующийся средней скоростью роста. Достигает в возрасте 10 лет 0,2 метра в высоту при диаметре 1,5 метра. Побеги ползучие по земле, с легко подымающимися концами. Хвоя мелкая, серебристо-голубого цвета (зимой чуть фиолетовая). Требования к почве и влаге невысокие. Рекомендуется для каменистых садов, а также как почвопокровник. Плотность посадки: 2 шт./м2.', '100-1000', 'normal', 'sun', '0.2м высота, диаметр 1.5м', 'blueChip'),
(9, 'Можжевельник средний<br>Blue and Gold', 'Оригинальный кустарниковый сорт можжевельника, относительно медленнорастущий, достигающий в возрасте 10 лет 0,8 метров в высоту при диаметре 1 метр. Побеги голубого и золотисто-жёлтого цвета, расположены неправильно. Требования к почве и влаге невысокие. Рекомендуется для малых садов, одиночных посадок и композиций.', '100-1000', 'normal', 'sun', '0.8м высота, диаметр 1м', 'blueAndGold'),
(10, 'Можжевельник чешуйчатый<br>Loderi', 'Карликовый  медленнорастущий кустарник почти конической формы. В возрасте 10 лет достигает 0,8 метров высоты. Хвоя имеет форму иголок зелёно-серой окраски. Сорт неустойчив к сухим зимним ветрам. Нуждается в укрытии. Рекомендуется для приусадебных, японских и каменистых садов.', '100-1000', 'normal', 'sun', '0.8м', 'loderi'),
(11, 'Можжевельник чешуйчатый<br>Nana', 'Невысокий кустарник с побегами стелющимися по земле, медленнорастущий, в возрасте 10 лет достигает 0,2 метра высотой при диаметре 1-1,5 метров. Побеги жёсткие, густые, хвоя короткая, зелено-голубого цвета. Требования к почве невысокие. Рекомендуются для каменистых садов. Иногда встречается в продаже штамбовая форма, для выращивания в контейнерах, на балконах и террасах.', '100-1000', 'normal', 'sun', '0.2м высота, 1-1.5м диаметр', 'nana'),
(12, 'Можжевельник казацкий<br>Variegata', 'Медленнорастущая карликовая форма. В возрасте10 лет достигает 0,4 м выс. при диам. 1 м. Побеги распростерты, верхушки побегов изогнутые, зеленого цвета. Характерная черта - бело-пестрая окраска веточек, нетребовательна к почве и влаге. Рекомендуется для небольших приусадебных и каменистых садов, садовых композиций.', '100-1000', 'normal', 'sun', '0.4м высота, 1м диаметр', 'variegata'),
(13, 'Можжевельник скальный<br>Blue Arrow ', 'Один из самых узких, колоновидных медленнорастущих сортов можжевельника. В возрасте 10 лет достигает 2-3 м выс. Побеги жесткие, расположены вертикально, плотно примыкают к стволу. Хвоя голубая имеет форму чешуи, не колючая. Требования к почве и влаге невысокие. Рекомендуется для каменистых садов, маленьких приусадебных садов, вересковых садиков.', '100-1000', 'normal', 'sun', '2-3м', 'blueArrow'),
(14, 'Можжевельник скальный<br>Skyrocket', 'Сорт характеризующийся узким, стройным габитусом и сильным ростом. В возрасте 10 лет достигает более 3 м выс. Побеги прямые, примыкающие к стволу. Окраска сине-зелёная. Требования к почве и влаге средние. Рекомендуется для небольших приусадебных садов как  горизонтальный элемент в растительных композициях.', '100-1000', 'normal', 'sun', '3м', 'skyrocket'),
(15, 'Можжевельник чешуйчатый<br>Holger', 'Невысокая кустарниковая форма, характеризующаяся умеренно быстрым ростом. В возрасте 10 лет достигает 0,8 м выс. при диам. 1,5 м. Хвоя, серебристо-синяя. Молодые весенние приросты имеют золотисто-желтую окраску. Требования к почве и влаге невысокие. Рекомендуется для каменистых и вересковых садов.', '100-1000', 'normal', 'sun', '0.8м высота, 1.5м диаметр', 'Holger'),
(16, 'Можжевельник виргинский<br>Hetz', 'Кустарниковый, быстрорастущий сорт можжевельника, достигающий в возрасте 10 лет более 1 м выс. при диам. 22,5 м. Ветви распростерты над землей, а у старых экземпляров расположены косо. Хвоя синеватая, вначале плоская, позже округлая. требования к почве и влаге невысокие. Рекомендуется для больших садов и парков.', '100-1000', 'normal', 'sun', '1м высота, 22.5м диаметр', 'Hetz'),
(17, 'Лиственница европейская', 'Дерево со стройным стволом и конической кроной. Сильнорослое, в возрасте 30 лет достигает 15 м выс. Хвоя нежная, ярко-зеленая, осенью - золотисто-желтая, на зиму опадает. Боковые побеги поникающие. Нуждается в плодородных и умеренно влажных, проницаемых почвах. Широко используется в озеленении городов и промышленных территорий. Хорошо переносит обрезку и формирование, высаживается в виде живой изгороди через каждые 0,5 м.', '100-1000', 'normal', 'sun', '15м', 'listvEuro'),
(18, 'Ель канадска<br>Conica', 'Карликовый, очень популярный сорт ели, имеющий вид правильных плотных конусов. В возрасте 10 лет достигает 1 м выс. Побеги тонкие, жесткие. Хвоя короткая, зеленая. Требования к почве и влаге средние. Рекомендуется для каменистых или вересковых садов, для выращивания в контейнерах. Растение - часто повреждается сосновым паутинным клещём.', '100-1000', 'normal', 'sun', '1м', 'Conica'),
(19, 'Тис ягодный', 'Двудомное дерево или большой кустарник. Растёт медленно, в возрасте 30 лет достигает 3-4 м в выс. В молодом возрасте крона коническая, с возрастом становится полушаровидной. Хвоя темно-зеленая, расположена на побегах спирально, на боковых веточках попарно. На женских особях образуются многочисленные декоративные семена, заключенные в ягодообразный ярко-красный присемянник. Нуждается в питательной, влажной, но проницаемой почве. Рекомендуется для создания стриженых изгородей и фигурных композиций.', '100-1000', 'normal', 'sun', '3-4м', 'tissBerry'),
(20, 'Тис ягодный<br>Fastigiata Aureomarginata ', 'Крона узкоколонновидная, достигает в возрасте 10 лет 1,2 м в выс. Главные побеги вертикальные, боковые ветви короткие. Хвоя золотисто-желтая с зеленой полоской. Нуждается в плодородных, влажных почвах на защищенном месте. Рекомендуется для небольших садов в теплых районах.', '100-1000', 'normal', 'sun', '1.2м', 'fastigiataAureomarginata'),
(21, 'Тис средний<br>Hicksii ', 'Крупный быстрорастущий кустарник, достигающий после 10 лет 3 м выс. Побеги приподнятые, жесткие хвоя темно-зеленого цвета. Обильно завязывает декоративные семена, покрытые красной оболочкой. Нуждается в плодородной и влажной почве. Устойчив к загрязнению воздуха. Используется для низкой стриженной живой изгороди. Плотность посадки в ряду: 2-3 шт./пол м.', '100-1000', 'normal', 'sun', '3м', 'Hicksii'),
(22, 'Туя западная<br>Danica', 'Карликовый, шаровидный кустарник, достигающий в возрасте 10 лет 0,4 м в диам. Побеги короткие, плотно расположенные, светло-зеленого цвета. Нуждается в довольно плодородной и влажной почве. Рекомендуется для маленьких приусадебных участков, каменистых садов, кладбищ.', '100-1000', 'normal', 'sun', '0.4м', 'Danica'),
(23, 'Туя западная<br>Smaragd', 'Один из наилучших конических сортов туи, характеризующийся умеренным ростом, достигающий после 10 лет 2,5 м выс. Ветви нежные, темно-зеленого цвета, не коричневые в зимний период. Нуждается довольно плодородной и влажной почве. Рекомендуется для живых изгородей, посадки на кладбищах и для выращивания в контейнерах. Частота посадки в ряду:  через  0,5-0,6 м.', '100-1000', 'normal', 'sun', '2.5м', 'Smaragd'),
(24, 'Туя западная<br>Spirals', 'Дерево с узкой конической кроной, в возрасте 10 лет достигает 3 м. Побеги винтообразные, повернуты так, что напоминают спираль. Хвоя темно-зеленая. Нуждается в плодородных и влажных почвах. Рекомендуется для одиночных посадок.', '100-1000', 'normal', 'sun', '3м', 'Spirals');

